/**
 * Convert an array of numbers into a DataView.
 */
export function numbersToDataView(value: any): DataView;
/**
 * Convert a DataView into an array of numbers.
 */
export function dataViewToNumbers(value: any): number[];
/**
 * Convert a string into a DataView.
 */
export function textToDataView(value: any): DataView;
/**
 * Convert a DataView into a string.
 */
export function dataViewToText(value: any): string;
/**
 * Convert a 16 bit UUID into a 128 bit UUID string
 * @param value number, e.g. 0x180d
 * @return string, e.g. '0000180d-0000-1000-8000-00805f9b34fb'
 */
export function numberToUUID(value: any): string;
export function hexStringToDataView(value: any): DataView;
export function dataViewToHexString(value: any): string;
export function webUUIDToString(uuid: any): string;
export function mapToObject(map: any): {};
